$(document).ready(function () {
    var table = $("#tableEmploye").DataTable({
        "processing": true,
        "ajax": "/search",
        "columns": [
            {
                data: 'id',
                name: 'id'
            },
            {
                data: 'nombre',
                name: 'nombre'
            },
            {
                data: 'apellido',
                name: 'apellido'
            },
            {
                data: 'direccion',
                name: 'direccion'
            },
            {
                data: 'correo',
                name: 'correo'
            },
            {
                data: 'fechaNac',
                name: 'fechaNac'
            },
            {
                data: 'genero',
                name: 'genero'
            },
            { defaultContent: "<button class='btn btn-danger'><i class='fas fa-trash-alt' style='color: white'></button>" }
        ],
        language: {
            "decimal": "",
            "emptyTable": "No hay informaci√≥n",
            "info": "Mostrando _START_ a _END_ de _TOTAL_ Entradas",
            "infoEmpty": "Mostrando 0 to 0 of 0 Entradas",
            "infoFiltered": "(Filtrado de _MAX_ total entradas)",
            "infoPostFix": "",
            "thousands": ",",
            "lengthMenu": "Mostrar _MENU_ Entradas",
            "loadingRecords": "Cargando...",
            "processing": "Procesando...",
            "search": "Buscar:",
            "zeroRecords": "Sin resultados encontrados",
            "paginate": {
                "first": "Primero",
                "last": "Ultimo",
                "next": "Siguiente",
                "previous": "Anterior"
            }
        },
    });
    $("#tableEmploye").on('click', 'tr', function () {
        console.log(table.row(this).data());
        var rowData = table.row(this).data();
        var id = rowData.id;
        $.ajax({
            url: "/delete/"+id,
            type: "DELETE",
            success: function (response) {
                alert(response)
                $('#tableEmploye').DataTable().ajax.reload();
            },
            error: function (error) {
                console.log(error);
            }
        })
    })
});
